import React, { useEffect, useState } from "react";

export default function Passages() {
  const [passages, setPassages] = useState([]);
  const API_URL = "http://localhost:3000/api/passages"; // Change ici si besoin

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(data => setPassages(data));
  }, []);

  return (
    <div className="container mt-4">
      <h2>Historique des passages</h2>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>UID</th>
            <th>Heure d'arrivée</th>
            <th>Heure de sortie</th>
          </tr>
        </thead>
        <tbody>
          {passages.map(p => (
            <tr key={p.id}>
              <td>{p.uid}</td>
              <td>{p.horodatage_arrivee}</td>
              <td>{p.horodatage_sortie || "—"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
